mapper.insert:

INSERT INTO book (
	book_no
	, mem_id
	, checkin
	, checkout
	, person
	, book_date
	, book_cancel
	, breakfast
	, branch
	, roomtype
	, payment_op
	, payment
	, charge
) VALUES (
	#{book_no}
	,#{mem_id}
	,#{checkin}
	,#{checkout}
	,#{person}
	,default
	,default
	,#{breakfast}
	,#{branch}
	,#{roomtype}
	,#{payment_op}
	,default
	,#{charge}
)

mapper.select:


    	SELECT 	COUNT(*)
		FROM    BOOK
		WHERE   BRANCH = #{branch}
		AND     ROOMTYPE = #{roomtype}
		AND 
		(
		    (#{checkin} BETWEEN CHECKIN AND CHECKOUT) OR (#{checkout} BETWEEN CHECKIN AND CHECKOUT)
		)
		AND #{checkout} != CHECKIN
		AND #{checkin} != CHECKOUT
